package com.loop2;

public class factorial {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int n = 5;
    int c =1;
    	for(int i=2;i<=n;i++) {
    		
    		 c =c*i;
    		System.out.println(c);
    	}
    	System.out.println("Factorial of "+n+" is: "+c); 
      }
	  // 5! =120  /5x4x3x2x1

}
